class Solution {
    fun numberOfAlternatingGroups(colors: IntArray, k: Int): Int {
    var res = 0
    var cons = 0
    for (i in 1 until colors.size + k - 1){
        val index = getIndex(i, colors.size)
        val prev = getIndex(i-1, colors.size)
        if (colors[index] != colors[prev]){
            cons++
            if (cons >= k-1) res++
        } else {
            cons = 0
        }
    }
    return res
}

fun getIndex(i: Int, size: Int): Int{
    return if (i < size) i else i - size
}
}
