class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        # 以nums[i] 结尾的交替数组长度
        dp = [0] * 2
        ans = 0
        n = len(colors)
        for i in range(n + k - 1):
            x = colors[i % n]
            dp[x] = dp[x ^ 1] + 1
            dp[x ^ 1] = 0
            if dp[x] >= k:
                ans += 1
        return ans
