#define pb push_back
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n= v.size();
        for( int i=0; i<n; i++) v.pb( v[i] );
        int i=0 , j=1 , ans=0;
        while( i+1<n && v[i]==v[i+1] ) i++;
        j=i+1;
        while( i<n ){
            while( j-i+1 < k && v[j]!=v[j+1] ){
                j++;
            }
            if( j-i+1==k ){
                ans++;
                i++;
            }
            else {
                i=j+1;
                j++;
            }
        }
        return ans;
    }
};
