class Solution {
public:
    int freq[2];


    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int ans = 0, n = a.size(), bad = 0;
        for (int i = 1; i < k; i++) {
            if (a[i] == a[i - 1]) {
                bad++;
            }
        }

        if (bad == 0) {
            ans++;
        }



        for (int i = 1; i < n; i++) {
            if (a[i] == a[i - 1]) {
                bad--;
            }
            if (a[(k - 2 + i) % n] == a[(k - 1 + i) % n]) {
                bad++;
            }
            if (bad == 0) {
                ans++;
            }

        }
        return ans;

    }
};
