class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        for (int i = 0; i < k-1; ++i) {
            c.push_back(c[i]);
        }
        queue<int> que;
        for (int i = 0; i < k-1; ++i) {
            if (c[i] == c[i+1]) {
                que.push(i);
            }
        }
        int ans = 0;
        for (int i = k-1; i < c.size(); ++i) {
            if (que.size() && que.front() == i-k) {
                que.pop();
            }
            if (que.empty()) {
                ++ans;
            }
            if (i < c.size()-1 && c[i] == c[i+1]) {
                que.push(i);
            }
        }
        return ans;
    }
};
