class Solution
{
public:
    int numberOfAlternatingGroups(vector<int> &colors, int k)
    {
        const int n = colors.size();
        int t = 1, res = 0;
        for (int i = 1; i < n + k - 1; i++)
        {
            if (colors[i % n] != colors[(i - 1) % n])
                t++;
            else
                t = 1;
            if (t >= k)
                res++;
        }
        return res;
    }
};
