constexpr int NN = 2e5+10;
int p[NN];
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0, n = colors.size(), i0=-1;
        for(int i=0; i<n; ++i){
            p[i] = p[i+n] = colors[i];
            if(colors[i]==colors[(i+1)%n]){
                i0 = i+1;
            }
        }
        p[2*n] = p[0];
        if(i0==-1){
            return n;
        }
        // printf("i0 = %d\n", i0);
        for(int i=i0, l=1; i<i0+n; ++i){
            if(p[i]!=p[i+1]){
                ++l;
                // printf("i=%d, l=%d\n", i, l);
                if(l>=k){
                    ++ans;
                }
            } else {
                l=1;
            }
        }
        return ans;
    }
};
