class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
            int n=c.size();
             for(int i=0;i<k-1;i++){
                 c.push_back(c[i]);
             }


        int x=1;
        long long ans=0;
        for(int i=1;i<c.size();i++){
                  if(c[i]!=c[i-1]){
                        x++;
                  }else{

                      if(x>=k){
                             ans+= x-k+1;
                      }
                        x=1;
                  }
        }


        if(x>=k) ans+=x-k+1;
        return ans;
    }
};
