class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = (int) v.size();
        int ans = 0;
        int cnt = 0;
        for (int i = 1; i < k; i++) {
            cnt += (v[i] ^ v[i - 1]);
        }
        ans += (cnt == k - 1);
        for (int i = 1; i < n; i++) {
            cnt -= (v[i] ^ v[i - 1]);
            cnt += (v[(i + k - 1) % n] ^ v[(i + k - 2) % n]);
            ans += (cnt == k - 1);
        }
        return ans;
    }
};
