class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int res = 0;
        vector<int> v(2 * n);
        for(int i = 0; i < n; i ++) v[i] = colors[i];
        for(int i = n; i < 2 * n; i ++) v[i] = colors[i - n];
        for(int i = 0; i < n * 2; i ++) {
            int j = i;
            while(j + 1 < n + k - 1 && v[j + 1] != v[j]) {
                j ++;
            }
            int len = j - i + 1;
            if (i < n) {
                res += max(0, len - k + 1);
            }
            i = j;
        }
        return res;
    }
};
