class Solution:
    def numberOfAlternatingGroups(self, nums: List[int], k: int) -> int:
        n=len(nums)
        nums=nums+nums
        # status=[False for i in range(n)]
        # status[0]=True
        op=0
        ws=0
        for we in range(1,2*n):        
            if nums[we]!=nums[we-1]:
                if we-ws+1>=k:
                    op+=1
            else:
                ws=we

            if we-ws+1>=k:
                ws+=1

            if ws>=n:
                break



        return op
