class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        for(int i=0;i<n;i++){
            a.push_back(a[i]);
        }
        int prev=-1;
        int count=0;
        int ans=0;
        map<int,int> mp;
        bool flag=true;
        for(int i=0;i<a.size();i++){
            if(a[i]!=prev){
                prev=a[i];
                count++;
            }else{
                count=1;
                prev=a[i];
            }
            mp[i]=count;
        }
        if(count==2*n) return n;
        count=0;
        prev=-1;
        for(int i=0;i<a.size();i++){
            if(a[i]!=prev){
                prev=a[i];
                count++;
            }else{
                if(i<n)ans+=max(0,count-k+1);
                if(i>=n){     
                    break;
                }
                count=1;
                prev=a[i];
            }
            mp[i]=count;
        }
        ans+= max(0,min(mp[n-1],count-k+1));
        return ans;
    }
};
