class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors = colors + colors
        last_bad = 0 
        n = len(colors)//2
        answer = 0
        last_bad = -1
        for i in range(2*n):
            if i > 0 and colors[i]==colors[i-1]:
                last_bad = i-1 
            if i >= k-1 and n-1 >= i-k+1 > last_bad:
               # print([i-k+1, i])
                answer+=1
        return answer
