class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for (int i = 0; i < n; ++i) colors.push_back(colors[i]);

        int ans = 0;
        for (int i = 0; i < n; ++i) {
            int j = i + 1;
            while (j < colors.size() && colors[j] != colors[j - 1]) j += 1;
            int c = j - i;
            if (c >= k) {
                // i ~ i + k - 1
                // n - 1 ~ n - 1 + k - 1 = n + k - 2
                int r = min(n + k - 1, j);
                c = r - i;
                ans += max(0, c - k + 1);
            }
            i = j - 1;
        }

        return ans;
    }
};
