class Solution {
    #define pb push_back
    #define DBG(x) cout << #x " = " << x << endl
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        const int n = colors.size();
        for(int i = 0;i<n;i++){
            colors.pb(colors[i]);
        }
        colors.pb(2);
        int res = 0;
        for(int l = 0,r;l<n;){
            r = l+1;
            while(r<2*n && colors[r]!=colors[r-1]){
                r++;
            }
            //DBG(l);
            //DBG(r);
            if(r-l >= k){
                res += min(r-l-k+1,n-l);
                DBG(res);
            }
            l = r;
        }
        return min(res,n);
    }
};
