class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int kk) {
        vector<int>v;
        for(auto i:colors)v.push_back(i);
        for(auto i:colors)v.push_back(i);
        int n=colors.size();
        vector<int>tmp(n,0);
        for(int i=0;i<n;i++){
            int j=i;
            while(j<v.size()-1&&v[j]!=v[j+1])j++;
            int k=j-i+1;
            for(int p=i;p<=min(j,n-1);p++)tmp[p]=k--;
            i=j;
        }
        int ans=0;
        for(int i=0;i<n;i++)if(tmp[i]>=kk)ans++;
        return ans;
    }
};
