class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> new_c;

        for(auto x: colors){
            new_c.push_back(x);
        }
        for(auto x: colors){
            new_c.push_back(x);
        }
        int n = colors.size();
        int last = 0;
        int ans = 0;
        for(int i = 1; i < 2 * n && last < n && (i - k + 1) < n; i++){
            if(new_c[i] == new_c[i - 1])last = i;
            if(i - last + 1 >= k)ans++;

        }


        return ans;
    }
};
