class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        for i in range(k-1):
            colors.append(colors[i])
        n = len(colors)
        cs = [1]*n
        ans = 0
        for i in range(1, n):
            if colors[i] != colors[i-1]:
                cs[i] = cs[i-1]+1
            if cs[i] >= k:
                ans += 1
        return ans
