class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> diff_cnt;
        for(int i=0; i<2*n; i++){
            if(colors[(i)%n]!=colors[(i+1)%n]) diff_cnt.push_back(1);
            else diff_cnt.push_back(0);
        }

        int l = 0;
        int r = 0;
        int res = 0;
        int cur_cnt = 0;
        while(r<n+k-2){
            int cur = diff_cnt[r];
            cur_cnt += cur;
            while(l<r && r-l+1>=k){
                int prev = diff_cnt[l++];
                cur_cnt -= prev;
            }
            if(cur_cnt+1==k){
                res++;
                //printf("l:%d r:%d\n", l , r);
            }
            r++;
        }
        return res;
    }
};
