class Solution {
public:
    int a[100005], b[100005], cnt = 0, n;
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        n = colors.size();
        for(int i = 1; i <= n; ++i) a[i] = colors[i - 1];
        for(int i = 1, j; i <= n; i = j + 1){
            j = i;
            while(j < n && a[j] != a[j + 1]) ++j;
            b[++cnt] = j - i + 1;
        }
        if(cnt == 1 && a[1] != a[n]) return n;
        if(a[1] != a[n]){
            if(cnt > 1){
                b[1] = b[1] + b[cnt];
                --cnt;
            }
        }
        int ans = 0;
        for(int i = 1; i <= cnt; ++i) 
            if(b[i] >= k)
                ans += b[i] - k + 1;
        return ans;
    }
};
