class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors = colors + colors[:k-1]


        queue = collections.deque()
        ans = 0
        for x in colors:
            if not queue or x != queue[-1]:
                queue.append(x)
            else:
                queue = collections.deque([x])

            if len(queue) >= k:
                ans += 1
        return ans
