class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for (int i = 0; i < n; ++i)
            colors.push_back(colors[i]);

        int ans = 0;
        int cnt = 0;
        for (int i = 1; i < k; ++i) {
            cnt += (colors[i] ^ colors[i - 1]);
        }

        /*
        if (n == k) {
            for (int i = 1; i < n && cnt != k - 1; ++i) {
                cnt -= (colors[i] ^ colors[i - 1]);
                cnt += (colors[i + k - 1] ^ colors[i + k - 2]);
            }
            if (cnt == k - 1) ans = 1;
        } else {
            */
            ans += (cnt == k - 1);
            for (int i = 1; i < n; ++i) {
                cnt -= (colors[i] ^ colors[i - 1]);
                cnt += (colors[i + k - 1] ^ colors[i + k - 2]);
                ans += (cnt == k - 1);
            }
        //}

        return ans;
    }
};

