from typing import List


class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        a = colors + colors[:k - 1]
        n = len(a)
        c = 1
        ans = 0
        for i in range(1, n):
            if a[i] != a[i - 1]:
                c += 1
            else:
                c = 1
            if c >= k:
                ans += 1
        return ans
