class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int rem=1;
        for(int i=1;i<k;i++){
            if(colors[i]!=colors[i-1]) rem++;
            else rem=1;
        }
        int cnt=0;
        if(rem==k) cnt++;
        // cout<<rem<<" ";
        for(int i=1;i<n;i++){
            // cout<<i+k-1<<" "<<colors[(i+k-1)%n]<<" "<<colors[(i+k-2)%n]<<"\n";
            if(colors[(i+k-1)%n]!=colors[(i+k-2)%n]){
                if(rem<k) rem++; 
            }
            else rem=1;
            // cout<<rem<<" ";
            if(rem==k) cnt++;
        }
        // cout<<"\n";
        return cnt;
    }
};
