class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        vector<int> nums = colors;
        for(int i=0;i<k-1;i++) nums.push_back(colors[i]);
        int n = nums.size();
        vector<int> prefix(n,0);
        prefix[0] = 1;
        for(int i=1;i<n;i++) {
            if(nums[i]!=nums[i-1]) prefix[i] = prefix[i-1]+1;
            else prefix[i] = 1;
            if(prefix[i]>=k) ans++;
        }
        return ans;
    }
};
