class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        count = 0

        if k == 1:
            return n

        alternating_length = 1
        index = 1
        while index < n + k - 1:
            current = index
            previous = index - 1

            if current >= n:
                current -= n
            if previous >= n:
                previous -= n

            if colors[current] != colors[previous]:
                alternating_length += 1
            else:
                alternating_length = 1

            if alternating_length >= k:
                count += 1

            index += 1

        return count
