class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors = colors * 2
        tmp = []
        for i in range(n + k - 1):
            tmp.append(colors[i] ^ colors[i+1])

        acc = list(accumulate(tmp, initial=0))
        ans = 0
        for i in range(len(tmp)):
            if i + k - 1 >= len(tmp): break
            if acc[i+k-1] - acc[i] == k - 1:
                ans += 1
        return ans
