class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0; i < k -1; i++)colors.push_back(colors[i]);
        int ans = 0;
        int how = 1;
        for(int i = 1; i < n + k -1; i ++){
           if(colors[i] != colors[i - 1])how++;
           else how = 1;
           ans += (how >= k);
        }
        return ans;
    }
};
