class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int>arr(2*n);
        for(int i=0;i<n;i++){
            arr[i] = colors[i];
        }
        for(int i=0;i<n;i++){
            arr[i+n] = colors[i];
        }

        int falseCounter = k-1;
        int ans = 0;
        for(int i=1;i<n+k-1;i++){
            if(arr[i] != arr[i-1]){
                falseCounter = max(falseCounter - 1,0);
                if(falseCounter == 0){
                    ans++;
                }
            }
            else{
                falseCounter = k-1;
            }
        }

        return ans;
    }
};
