class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        N = len(colors)
        dp = [[1] * 2 for _ in range(N)]

        for i in range(N * 2):
            i %= N
            color = colors[i]
            prev = (i - 1) % N
            if color != colors[prev]:
                dp[i][color] = dp[i - 1][1 - color] + 1
            else:
                dp[i][color] = 1

        return sum(1 for ls in dp for l in ls if l >= k)
