class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, long long k) {
        int n = a.size();
        vector<long long> pre(n);
        for(int i=0;i<n;i++) {
            int j = (i+1)%n;
            pre[j] = a[i] != a[j];
        }
        for(int i=1;i<n;i++) pre[i] += pre[i-1];

        long long ans = 0;
        for(int i=0;i<n;i++) {
            int j = (i + k - 1)%n;
            if(j > i) {
                ans += pre[j] - pre[i] == k - 1;
            }
            else {
                ans += pre[n-1] - pre[i] + pre[j] == k - 1;
            }
        }
        return ans;
    }
};
