class Solution:
    def numberOfAlternatingGroups(self, c: List[int], k: int) -> int:
        n=len(c)
        dp=[1]*n
        for i in range(1,n):
            if c[i]!=c[i-1]:
                dp[i]=dp[i-1]+1
        ans=0
        #print(dp)
        for i in range(n):
            if i>=k-1:
                ans+=dp[i]>=k
            else:
                if c[0]!=c[-1] and dp[i]==i+1:
                    ans+=((dp[i]+dp[-1])>=k)
            #print(i,ans)
        return ans
