class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> a;
        for(auto &i : colors) a.push_back(i);
        int n = a.size();
        for(int i = 0; i < min(k - 1, n); i++) {
            a.push_back(a[i]);
        }
        int ans = 0; n = a.size();
        for(int i = 0; i < n; ) {
            int j = i + 1, cnt = 1;
            while(j < n && a[j] != a[j - 1]) {
                j++;
                cnt++;
            }
            ans += max(0, cnt - k + 1);
            i = j;
        }
        return ans;
    }
};
