class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors = colors[-k+1:] + colors
        ans = 0
        cnt = 1
        for i in range(1,len(colors)):
            if colors[i]!=colors[i-1]:
                cnt += 1
            else:
                if cnt >=k and i>=k:
                    ans += cnt-k+1
                cnt = 1
        if cnt >=k:
            ans += cnt - k +1
        return ans
