class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        a = colors
        n = len(a)
        a += colors
        a += colors
        j = 0
        ans = 0
        i = 0
        while i < n:
            # i is the starting point
            if(i == j):
                j += 1
                start = -1
                while i + k != j:
                    if(a[j] == a[j - 1]):
                        start = j
                        break
                    j += 1
                if(start == -1):
                    ans += 1
                    i += 1
                else:
                    i = start
            else:
                start = -1
                while i + k != j:
                    if(a[j] == a[j - 1]):
                        start = j
                        break
                    j += 1
                if(start == -1):
                    ans += 1
                    i += 1
                else:
                    i = start
        return ans
