/**
 * @param {number[]} colors
 * @param {number} k
 * @return {number}
 */
var numberOfAlternatingGroups = function(colors, k) {
  for (let i = 1; i < k; ++i) {
    colors.push(colors[i - 1]);
  }

  let n = colors.length;
  let l = 0;
  let res = 0;

  for (let r = 1; r < n; ++r) {
    if (colors[r] == colors[r-1]) l = r;
    if (r - l + 1 >= k) res++;
  }

  return res;
};
