class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int l = 1;
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        int n = colors.size();
        int ans = 0;
        for(int i=1;i<n;i++){
            if(colors[i]==colors[i-1]){
                if(l>=k){
                    ans+=(l-k+1);
                }
                l = 1;
            }
            else{
                l++;
            }
        }
        if(l>=k) ans+=(l-k+1);
        return ans;
    }
};
