class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(),ans=0;
        vector<int> arr;
        for(int i=n-k;i<=n-1;i++){
            arr.push_back(colors[i]);
        }
        for(int i=0;i<colors.size();i++){
          arr.push_back(colors[i]);
        }
        arr.push_back(arr.back());
        int cnt=0;
        for(int i=0;i<arr.size()-1;i++){
          //cout<<arr[i]<<" "<<" ";

          if(arr[i]!=arr[i+1]){
           cnt++; 
          }
          else{
             cnt=1;
          }
         // cout<<cnt<<" ";
          if(i>=k-1 && cnt>=k){
              ans++;
          }

        }
        cout<<"\n";
        return ans;

    }
};
