class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int i = 0, j = k - 1, n = v.size();

        int ct = 0;
        vector<int>d(n);
        for(int i = 0; i<n; i++)
        {
            if(v[i] != v[(i+1) % n]) d[i] = 1;
        }

        int val = 0, ind = -1;

        for(int i = 0; i<n; i++)
        {
            if(d[i] == 0)
            {
                ind = i;
                break;
            }
        }

        if(ind == -1)
        {
            return n;
        }

        for(int i = ind; i<(ind + n); i++)
        {
            if(d[(i % n)] == 1)
            {
                val++;
                if(val == k - 1)
                {
                    ct++;
                    val--;
                }
            }
            else
            {
                val = 0;
            }
        }

        return ct;
    }
};
