class Solution:
    def numberOfAlternatingGroups(self, c: List[int], k: int) -> int:
        n = len(c)
        res = 0
        prev = 0
        for r in range(n+k-1):
            if c[r%n]==c[(r-1)%n]:
                prev = r
            res+=r>=prev+k-1
            # print(r, prev, res)
        return res
