class Solution {
    int a[200005], n;
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        n = colors.size();
        int rst = 0;
        for(int i = 1; i <= n; i += 1)
            a[i] = colors[i-1], a[i+n] = colors[i-1];
        int l = 1, r = 1;
        while(r < n + k) {
            if(r - l + 1 < k) {
                r += 1;
                if(a[r-1] == a[r]) {
                    l = r;
                }
            } else if(r - l + 1 == k) {
                l += 1;
                rst += 1;
            }
        }
        return rst;
    }
};
