class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>v = colors;
        for(int i = 0;i<colors.size();i+=1){
            v.push_back(colors[i]);
        }
         for(int i = 0;i<colors.size();i+=1){
            v.push_back(colors[i]);
        }
        int ans = 0;
        int m = colors.size();
        set<int>st;
        for(int i = 0;i+1<v.size();i+=1){
            if(v[i] == v[i+1]){
                st.insert(i);
            }
        }

        for(int i = 0;i<m;i+=1){
            int l = i;
            int r = i+k-1;

            auto it = st.lower_bound(l);
            if(it == st.end()){
                ans+=1;
            }
            else{
                int ind = *it;
                if(ind>=l && ind<r){
                    continue;
                }
                if(ind>=r){
                    ans+=1;
                    continue;
                }
                if(it != st.begin()){
                    it--;
                    if(*it != l-1){
                        ans+=1;
                        continue;
                    }
                }

            }
        }
        return ans;

    }
};
