class Solution:
    '''notes
    * n <= 1e5: O(N Log N).
    '''
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        nGroups = 0
        altLen = 0
        n = len(colors)
        # print(f'* colors={colors}, k={k}')
        for i1 in range(1, n + k - 1):
            j0 = (i1 - 1) % n
            j1 = i1 % n
            if colors[j0] == colors[j1]:
                altLen = 0
            else:
                altLen += 1
            # print(f'i1={i1}, altLen={altLen}')
            if altLen >= k - 1:
                nGroups += 1
        return nGroups
