class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        vector<int>v(nums.begin(),nums.end());
        for(int i=0;i<k-1;i++)v.push_back(nums[i]);

        int n=v.size();
        int cnt=1;
        int ans=0;
        for(int i=1;i<n;i++)
        {
            if(v[i-1]!=v[i]){
                cnt++;
            }
            else{
                ans+=(cnt>=k?cnt-k+1:0);
                cnt=1;
            }
        }
        if(cnt>=k)ans+=cnt-k+1;
        return ans;
    }
};
