class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int res=0;
        int n=a.size();
        int t=0;
        for(int i=0;i<n+k-2;++i){
            if(a[i%n]+a[(i+1)%n]==1){
                ++t;
                if(t>=k-1){
                    ++res;
                }
            }
            else{
                t=0;
            }
        }
        return res;
    }
};
