class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors.extend(colors[:k-1])
        l = 0
        ans = 0
        for r in range(1,len(colors)):
            if colors[r]^colors[r-1]:
                if r-l+1>=k:ans+=1
            else:l = r
        return ans
