class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int l=colors.length;
        List<Integer> inds=new ArrayList<Integer>();
        for(int i=0;i<l-1;i++){
            if(colors[i]==colors[i+1])
                inds.add(i);
        }
        if(colors[l-1]==colors[0])
            inds.add(l-1);
        int res=0;
        for(int i=0;i<inds.size()-1;i++){
            if(inds.get(i+1)-inds.get(i)>=k)
            res+=inds.get(i+1)-inds.get(i)-k+1;
        }

        if((inds.size()>=1) &&l+inds.get(0)-inds.get(inds.size()-1)>=k)
            res+=l+inds.get(0)-inds.get(inds.size()-1)-k+1;
        if(inds.size()>1)
            return res;
        if(inds.size()==1)
            return l-k+1;
        return l;
    }
}
