class Solution {
public:
    static int numberOfAlternatingGroups(std::vector<int>& colors, const int k) noexcept {
        for (size_t i = 0; i + 1 < k; ++i) {
            colors.emplace_back(colors[i]);
        }

        size_t left = 0;
        size_t right = 0;

        int res = 0;

        while (left < colors.size()) {
            ++right;

            while (right < colors.size() && colors[right] != colors[right - 1]) {
                ++right;
            }

            if (right - left >= k) {
                res += right - left - k + 1;
            }

            left = right;
        }

        return res;
    }
};
