func numberOfAlternatingGroups(nums []int, k int) int {
    n := len(nums)
    for i:= 0;i<n;i++{
        nums = append(nums, nums[i])
    }
    s := 0
    p := 1
    ans := 0
    for s < n {
        for nums[p] ^ nums[p-1] == 1 && p - s+1 < k  {
            p++
        } 
        if p - s + 1 == k && nums[p] ^ nums[p-1] == 1 {
            //fmt.Println("found", s, p, ans)
            ans ++
            s++
        }else {
            s = p
            p = s + 1
        }
      //  fmt.Println(s, p, ans)
    }
    return ans
}
