class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans =0;
        int n = colors.size();
        vector<int> tt = colors;
        for(int i=0;i<n;i++)tt.push_back(colors[i]);
        vector<int> vec(2*n,0);
        for(int i=1;i<2*n;i++){
            if(tt[i] == tt[i-1])vec[i] = 1;
            vec[i] += vec[i-1];
        }
        for(int i=0;i<n;i++){
            int x = vec[i+k-1] - (i>0 ? vec[i-1] : 0);
            // cout<<i<<" "<<x<<endl;
            if(i>0 && (tt[i] == tt[i-1]))x--;
            if(x ==0 )ans++;
        }

        return ans;
    }
};
